mapper.select:
SELECT COUNT(*) from t_user WHERE deleted = FALSE and phone_num=#{phone}
and DATE_FORMAT(create_time, '%Y-%m-%d')>= DATE_FORMAT(#{startTime}, '%Y-%m-%d')
and DATE_FORMAT(create_time, '%Y-%m-%d')<= DATE_FORMAT(#{endTime}, '%Y-%m-%d')

mapper.update:
UPDATE t_active

    `pic`=#{pic},
    `topic`=#{topic},
    `content`=#{content},
    `time`=#{time}

WHERE uuid=#{uuid}

UPDATE t_active

    deleted=TRUE

WHERE uuid=#{uuid}

update t_admin

    deleted = TRUE

WHERE uuid = #{uuid}

UPDATE t_sys_label


        `value` = #{label}


where `key`= #{key}

UPDATE t_scene

    deleted = TRUE

where uuid = #{uuid}

UPDATE t_scene


        `key` = #{key}


where uuid = #{uuid} and deleted = FALSE

update t_sys

   `value`=#{value},

WHERE `key`=#{key}

UPDATE t_user

    stop_back_money=#{key}

where id = #{userId}

UPDATE t_store_active
pic=#{request.pic}, topic=#{request.topic}, content=#{request.content}, money=#{request.money}, back_money=#{request.backMoney}, `size`=#{request.size}, `shop_pic`=#{request.shopPic}
where uuid = #{request.uuid}

mapper.insert:
insert into t_store_active(`uuid`,`pic`,`topic`,`content`,`size`,`money`,`shop_pic`,`back_money`)
SELECT #{request.uuid},#{request.pic},#{request.topic},#{request.content},#{request.size},#{request.money},#{request.shopPic},#{request.backMoney}
from dual where not EXISTS(select uuid from t_store_active where uuid =#{request.uuid} )

INSERT INTO t_user(nick_name,icon,picture,age,occupation,height,weight,city,`label`,zodiac,gender,uuid) VALUES
(#{item.nickName},#{item.icon},#{item.picture},#{item.age},#{item.occupation},#{item.height},#{item.weight}, #{item.city},#{item.label},#{item.zodiac},#{item.gender},#{item.uuid})
ON DUPLICATE KEY UPDATE nick_name = #{item.nickName},icon = #{item.icon},picture = #{item.picture},age=#{item.age},`occupation`=#{item.occupation}, `height`=#{item.height},`city`=#{item.city},`label`=#{item.label},`zodiac`=#{item.zodiac},`gender`=#{item.gender},`uuid`=#{item.uuid}

INSERT INTO t_orders(user_id,pay_type,uuid,start_time,sel_time_type,order_period,`person_count`,grateful_free,label,perhaps,scenes,place,money,longitude,latitude,address) VALUES
(#{item.userUuid},#{item.payType},#{item.uuid},#{item.startTime},#{item.selTimeType},#{item.orderPeriod},#{item.personCount}, #{item.gratefulFree},#{item.label},#{item.perhaps},#{item.scenes},#{item.place},#{item.money},#{item.longitude},#{item.latitude},#{item.address})
ON DUPLICATE KEY UPDATE user_id = #{item.userUuid},pay_type = #{item.payType},uuid = #{item.uuid},start_time=#{item.startTime},`sel_time_type`=#{item.selTimeType}, `order_period`=#{item.orderPeriod},`person_count`=#{item.personCount},`grateful_free`=#{item.gratefulFree},`label`=#{item.label},`perhaps`=#{item.perhaps},`scenes`=#{item.scenes} ,`place`=#{item.place},`money`=#{item.money},`longitude`=#{item.longitude},`latitude`=#{item.latitude},`address`=#{item.address}

mapper.select:
 SELECT * from t_party where  deleted=FALSE
and examine_and_verify=#{value}
limit #{pageNum},10

mapper.update:
UPDATE t_party set `examine_and_verify`=#{value}
 , `note` = #{note}
where `uuid` = #{uuid}

mapper.insert:
insert into t_coupons(`uuid`,`coupons_name`,`coupons_code`,`note`,`del_money`,`content_money`)
SELECT #{couponsRequest.uuid},#{couponsRequest.couponsName},#{couponsRequest.couponsCode},#{couponsRequest.note},#{couponsRequest.delMoney},#{couponsRequest.contentMoney}
from dual WHERE NOT EXISTS (SELECT  uuid from t_coupons WHERE uuid = #{couponsRequest.uuid})

mapper.update:
UPDATE t_coupons
coupons_name=#{couponsRequest.couponsName}, coupons_code=#{couponsRequest.couponsCode}, note=#{couponsRequest.note}, del_money=#{couponsRequest.delMoney}, content_money=#{couponsRequest.contentMoney}
where uuid = #{couponsRequest.uuid}

mapper.insert:
insert into t_tryst_scene (`uuid`,`pic`,`icon`,`name`,`type`,`sort`,`content`)
SELECT #{request.uuid},#{request.pic},#{request.icon},#{request.name},#{request.type},#{request.sort},#{request.content}
from dual where not exists (SELECT uuid from t_tryst_scene where uuid =#{request.uuid} )

insert into t_video_home (`uuid`,`video`,`video_pic`)
SELECT #{request.uuid},#{request.video},#{request.videoPic}
from dual where not exists (select uuid from t_video_home where uuid = #{request.uuid})
